import React, { Component } from 'react';
import './App.css';

var slideIndex = 1;
export class App extends Component{
  state = {
    landingPageData: {},
  }

  showSlides(n) {
    var i;
    var slides = document.getElementsByClassName("slide");
    if (n > slides.length) {slideIndex = 1}
    if (n < 1) {slideIndex = slides.length}
    for (i = 0; i < slides.length; i++) {
        slides[i].style.display = "none";
    }
    slides[slideIndex-1].style.display = "flex";
  }

  componentDidMount() {
    this.showSlides(1);
  }

plusSlides(n) {
    this.showSlides(slideIndex += n);  
    document.getElementById("pageNum").innerHTML = "Page " + String(slideIndex-1);
    if(slideIndex == 1){
        document.getElementById("pageNum").innerHTML = "";
    }
    //document.getElementById("n").style.display = "none";
    if(slideIndex >= 3){
        document.getElementById("p").style.display = "block";
    }
    else{
        document.getElementById("p").style.display = "none";
    }
}


toggle(id){
    var text = document.getElementById(id).style.display;
    if(text == "block"){
        document.getElementById(id).style.display = "none";
    }
    else{
        document.getElementById(id).style.display = "block";
    }
}

show(id){
    document.getElementById(id).style.display = "block";
}

hide(id){
    document.getElementById(id).style.display = "none";
}

showNext(){
    document.getElementById("n").style.display = "block";
}

playQuestion(){
    var question = document.getElementById("story-retel");
    document.getElementById("pause").style.display = "block";
    document.getElementById("play").style.display = "none";
    question.play();
}

pauseQuestion(){
    var question = document.getElementById("story-retel");
    document.getElementById("pause").style.display = "none";
    document.getElementById("play").style.display = "block";
    question.pause();
}
render(){
  return (
    <div>
      // Slideshow container 

      <div class="phoneText">Not available on this device</div>

      <div class="slideshow-container">
          <div class="title">Les  enfants de cire</div>
          <div class="slide-container">
              <div class="arrow-container">
                  <a class="next" id="p" onclick="plusSlides(-1)">&#10094;</a>
              </div>

              // Slides 
              // Intro Slide 
              <div class="slide fade" style="display: flex">
                  <div class="image-container">
                      <img class="image" src="/images/01.jpg"/>
                      <div class="avatar-container">
                          <img class="avatar" src="/images/Avatar.png"/>
                      </div>
                      <div id="intro" class="speech sb1">
                          Aujourd’hui, tu vas lire l’histoire « Les enfants de cire. » Sélectionnez la flèche pour passer à la page suivante.
                          <span class="success-checkmark" onclick="hide('intro'); showNext()"></span>
                      </div>
                  </div>

                  <div class="input-container">
                      <input class="input-box" placeholder="Enter Name"/>
                      <input class="input-box" placeholder="Enter ID"/>
                      <input class="input-box" placeholder="Enter RA Name"/>
                      <input class="input-box" placeholder="Enter Date"/>
                      <div class="begin-button" onclick="show('intro')">Press To Begin</div>
                  </div>
              </div>
              // Slide 1 
              <div class="slide fade">
                  <div class="image-container">
                      <img class="image" src="/images/02.jpg"/>
                      <div class="avatar-container">
                          <img class="avatar" src="/images/Avatar.png"/>
                      </div>
                  </div>


                  <div class="text-container">
                      <p>Il y a une famille bien heureuse. Ils ne se disputent jamais.
                          <span class="success-checkmark" onclick="showNext()"></span>
                      </p>
                  </div>
              </div>
              // Slide 2 
              <div class="slide fade">
                  <div class="image-container">
                      <img class="image" src="/images/03.jpg"/>
                      <div class="avatar-container">
                          <img class="avatar" src="/images/Avatar.png"/>
                      </div>
                  </div>


                  <div class="text-container">
                      <p>Ils aident leurs parents à la maison et dans les champs. 
                          <span class="success-checkmark" onclick="showNext()"></span>
                      </p>
                  </div>
              </div>
              // Slide 3 
              <div class="slide fade">
                  <div class="image-container">
                      <img class="image" src="/images/04.jpg"/>
                      <div class="avatar-container">
                          <img class="avatar" src="/images/Avatar.png"/>
                      </div>
                  </div>


                  <div class="text-container">
                      <p>Mais ils n’ont pas le droit de s’approcher d’un feu.
                          <span class="success-checkmark" onclick="showNext()"></span>
                      </p>
                  </div>
              </div>
              // Slide 4 
              <div class="slide fade">
                  <div class="image-container">
                      <img class="image" src="/images/05.jpg"/>
                      <div class="avatar-container">
                          <img class="avatar" src="/images/Avatar.png"/>
                      </div>
                  </div>


                  <div class="text-container">
                      <p>Ils doivent faire tout leur travail pendant la nuit. Puisqu’ils sont faits de cire! Mais un des garçons désire se promener au soleil.
                          <span class="success-checkmark" onclick="showNext()"></span>
                      </p>
                  </div>
              </div>
              // Slide 5 
              <div class="slide fade">
                  <div class="image-container">
                      <img class="image" src="/images/06.jpg"/>
                      <div class="avatar-container">
                          <img class="avatar" src="/images/Avatar.png"/>
                      </div>
                  </div>


                  <div class="text-container">
                      <p>Un jour son désir est trop puissant. Ses frères l’avertissent… 
                          <span class="success-checkmark" onclick="showNext()"></span>
                      </p>
                  </div>
              </div>
              // Slide 6 
              <div class="slide fade">
                  <div class="image-container">
                      <img class="image" src="/images/07.jpg"/>
                      <div class="avatar-container">
                          <img class="avatar" src="/images/Avatar.png"/>
                      </div>
                  </div>


                  <div class="text-container">
                      <p>Mais c’est trop tard! Il fond au soleil brûlant.
                          <span class="success-checkmark" onclick="showNext()"></span>
                      </p>
                  </div>
              </div>
              // Slide 7 
              <div class="slide fade">
                  <div class="image-container">
                      <img class="image" src="/images/08.jpg"/>
                      <div class="avatar-container">
                          <img class="avatar" src="/images/Avatar.png"/>
                      </div>
                  </div>


                  <div class="text-container">
                      <p>Les enfants de cire sont si tristes de voir leur frère fondre.
                          <span class="success-checkmark" onclick="showNext()"></span>
                      </p>
                  </div>
              </div>
              // Slide 8 
              <div class="slide fade">
                  <div class="image-container">
                      <img class="image" src="/images/09.jpg"/>
                      <div class="avatar-container">
                          <img class="avatar" src="/images/Avatar.png"/>
                      </div>
                  </div>


                  <div class="text-container">
                      <p>Mais ils font un plan. Ils façonnent le morceau de cire fondue en oiseau.
                          <span class="success-checkmark" onclick="showNext()"></span>
                      </p>
                  </div>
              </div>
              // Slide 9 
              <div class="slide fade">
                  <div class="image-container">
                      <img class="image" src="/images/10.jpg"/>
                      <div class="avatar-container">
                          <img class="avatar" src="/images/Avatar.png"/>
                      </div>
                  </div>


                  <div class="text-container">
                      <p>Ils apportent leur frère l’oiseau jusqu’au sommet d’une montagne haute.
                          <span class="success-checkmark" onclick="showNext()"></span>

                      </p>
                  </div>
              </div>
              // Slide 10 
              <div class="slide fade">
                  <div class="image-container">
                      <img class="image" src="/images/11.jpg"/>
                      <div class="avatar-container">
                          <img class="avatar" src="/images/Avatar.png"/>
                      </div>
                  </div>


                  <div class="text-container">
                      <p>Et lorsque le soleil se lève, il s’envole vers la lumière du matin en chantant.
                          <span class="success-checkmark" onclick="showNext()"></span>

                      </p>
                  </div>
              </div>
              // Question Slide Intro 
              <div class="slide fade">
                  <div class="question-prompt-container">
                      <p>Tu as lu l’histoire « Les enfants de cire. » 
                          Sélectionnez le bouton vert « RECORD » et dites-nous l’histoire du
                          début à la fin. Dites-nous tous les événements et détails dont vous
                          vous souvenez.</p>
                  </div>
                  <div class="audio-controls-container">
                      <audio id="story-retel">
                          <source src="/sounds/story_retail_instructions.mp3" type="audio/mpeg" />
                      </audio>
                      <img class="audio-button" id="play" src="/images/play.png" onclick="playQuestion()"/>
                      <img class="audio-button" id="pause" style="display: none;" src="/images/pause.png" onclick="pauseQuestion()"/>
                  </div>
              </div>
              // Question Slide 1 
              <div class="slide fade">
                  <div class="question-container">
                      <div class="question-title">Questions littérales</div>
                      <p class="question">1. Qu’est-ce que le frère oiseau fait lorsque le soleil se lève?</p>
                      <p class="question-option">a) il fait un plan. </p>
                      <p class="question-option">b) il s’envole vers la lumière du matin </p>
                      <p class="question-option">c) il se promène </p>
                      <p class="question-option">d) il aide ses parents </p>
                  </div>
              </div>
              // Question Slide 2 
              <div class="slide fade">
                  <div class="question-container">
                      <div class="question-title">Questions littérales</div>
                      <p class="question">2. Pourquoi est-ce que les enfants de cire sont si tristes? </p>
                      <p class="question-option">a) parce qu’ils ont une famille bien heureuse.  </p>
                      <p class="question-option">b) parce qu’ils aident leurs parents à la maison.  </p>
                      <p class="question-option">c) parce que leur frère fond au soleil brûlant.</p>
                      <p class="question-option">d) parce que leur frère l’oiseau s’envole. </p>
                  </div>
              </div>


              <div class="arrow-container">
                  <a class="next" id="n" onclick="plusSlides(1)">&#10095;</a>
              </div>
          </div>


          <div class="pageNum-container">
              <p id="pageNum"></p>
          </div>

      </div>
    </div>
  );
}
}

export default App;
